nc=len(football_partition)
print(nc)
content=[]
for s in range(nc):
    com=football_partition[s]
    for s1 in range(nc):
        if(s1!=s):
            com=com-football_partition[s1]
    if(com==set()):
        content.append(s)
print(content)

edges=open('data/football.txt').read()
edges= [item.split() for item in edges.split('\n')[:-1]]
edges=[tuple([int(i[0]),int(i[1])]) for i in edges if(i[0]!=i[1])]
nodes=list(set([i for (i,j) in edges]+[j for (i,j) in edges]))
print(len(nodes))


grafo=nx.Graph()
grafo.add_nodes_from(nodes)
grafo.add_edges_from(edges)
my_pos = nx.spring_layout(grafo, seed = 4)
plt.figure(1)
plt.figure(figsize=(8, 6))
paleta=['tab:blue','tab:orange','tab:green','tab:purple','tab:brown','tab:pink','tab:gray','tab:olive','tab:cyan',"purple","blue","blue","blue","blue","blue","blue"]
colores=["white" for i in nodes]
for i in range(len(nodes)):
    for s in range(len(football_partition)):
        if(nodes[i] in football_partition[s]):
            if(colores[i]=="white"):
                colores[i]=paleta[s]
            else:
                colores[i]="red"
nx.draw(grafo,pos=my_pos,node_size=100,with_labels=False,font_color="whitesmoke",node_color=colores)
